import scene from '@/threeModal/scene';
// 3.1导入相机配置
import camera from '@/threeModal/camera';
// 4.导入渲染器
import renderer from '@/threeModal/renderer';
// // 导入控制器
// import controls from '@/threeModal/controls'
// 导入渲染更新
import animate from '@/threeModal/animate';
// 导入初始化监听屏幕大小
import '@/threeModal/init';
// 5.1导入坐标轴
import axse from '@/threeModal/axseHelp';
import { ref, onMounted } from 'vue';

// 6.1获取场景div

export default {
  __name: 'SceneCanvas',
  setup(__props, {
    expose
  }) {
    expose();

    // 1.导入threejs
    // import * as THREE from 'three'
    // 2.导入(初始化)场景
    let sceneDiv = ref(null);

    // 3.2.将相机添加进场景
    scene.add(camera);

    // 5.2
    scene.add(axse);

    // vue挂载成功之后再去获取dom
    onMounted(() => {
      // 6.2将渲染器加入dom
      sceneDiv.value.appendChild(renderer.domElement);
      // 7.2渲染执行
      animate();
    });
    const __returned__ = {
      get sceneDiv() {
        return sceneDiv;
      },
      set sceneDiv(v) {
        sceneDiv = v;
      },
      get scene() {
        return scene;
      },
      get camera() {
        return camera;
      },
      get renderer() {
        return renderer;
      },
      get animate() {
        return animate;
      },
      get axse() {
        return axse;
      },
      ref,
      onMounted
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
