import * as THREE from 'three'
import vertex from '@/shader/lineRader/vertexShader.glsl'
import fragment from '@/shader/lineRader/fragmentShader.glsl'
import gsap from 'gsap'
export default class lineRader {
  constructor() {
    // 创建圆柱几何体
    this.geometry = new THREE.PlaneGeometry(1, 1)
    this.material = new THREE.ShaderMaterial({
      uniforms: {
        uTime: {
          value: 0
        },
        uColor: {
          value: new THREE.Color('#7D9EC0')
        }
      },
      vertexShader: vertex,
      fragmentShader: fragment,
      transparent: true,
      side: THREE.DoubleSide
    })

    this.mesh = new THREE.Mesh(this.geometry, this.material)
    this.mesh.position.set(3.5, 0.5, 2.5)
    this.mesh.rotation.x = - Math.PI / 2
    gsap.to(this.material.uniforms.uTime, {
      value: 30,
      ease: 'none',
      duration: 4,
    })
  }
}
