import { DRACOLoader } from 'three/examples/jsm/loaders/DRACOLoader'
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader'
import * as THREE from 'three'
import scene from '@/threeModal/scene'
import gsap from 'gsap'
import CameraModule from '@/threeModal/camera'
// import modityCityMaterial from '../modity/modityCityMaterial'
// import FlyLine from './flyLine'
// import FlyLineShader from './flyLineShader'
// import CreateMeshLine from './createMeshLine'
// import LineWall from './lineWall'
// import LineRader from './lineRader'
// import Alarm from './alarm'
export default class CreateCity {
  constructor () {
    let daoLoader = new DRACOLoader()
    let loader = new GLTFLoader()
    daoLoader.setDecoderPath('/glb/')
    daoLoader.setDecoderConfig({ type: 'js' })
    daoLoader.preload()
    loader.setDRACOLoader(daoLoader)
    // 加载城市模型
    loader.load('/glb/garden.glb', gltf => {
      console.log(gltf)
      gltf.cameras.forEach(item => {
        CameraModule.add(item.name, item.cameras)
      })
      gltf.scene.traverse(item => {
        // console.log(item)
        if (item.name === 'car') {
          this.car = item
        }
        // 根据点创建曲线

        if (item.name === 'NURBS_曲线002') {
          item.visible = false
          const points = []
          // console.log(item.geometry);
          for (
            let i = item.geometry.attributes.position.count - 1;
            i >= 0;
            i--
          ) {
            points.push(
              new THREE.Vector3(
                item.geometry.attributes.position.getX(i),
                item.geometry.attributes.position.getY(i),
                item.geometry.attributes.position.getZ(i)
              )
            )
          }
          // console.log(points)
          this.curve = new THREE.CatmullRomCurve3(points)
          // console.log(this.curve);
          this.curveProgress = 0
          gsap.to(this, {
            curveProgress: 0.9999,
            duration: 10,
            repeat: -1,
            onUpdate: () => {
              const point = this.curve.getPoint(this.curveProgress)
              this.car.position.set(point.x, point.y, point.z)
              if (this.curveProgress + 0.0001 < 1) {
                const point = this.curve.getPoint(this.curveProgress + 0.0001)
                this.car.lookAt(point)
                // console.log(point)
              }
              
            }
          })
        }
      })
      scene.add(gltf.scene)
    })

    // 添加飞线
    // const flyLine = new FlyLine()
    // scene.add(flyLine.mesh)

    // const flyLineShader = new FlyLineShader()
    // scene.add(flyLineShader.mesh)

    // // 添加光墙
    // const lineWall = new LineWall()
    // scene.add(lineWall.mesh)

    // // 添加雷达
    // const lineRader = new LineRader()
    // scene.add(lineRader.mesh)

    // // 添加警告标识
    // const alarm = new Alarm()
    // scene.add(alarm.mesh)
    // alarm.onClick(() => {
    //   alert('警告')
    // })
  }
}
