import scene from '@/threeModal/scene';
// 3.1导入相机配置
import camera from '@/threeModal/camera';
// 4.导入渲染器
import renderer from '@/threeModal/renderer';
import controls from '@/threeModal/controls';
// 5.1导入坐标轴
import axse from '@/threeModal/axseHelp';
import { ref, onMounted } from 'vue';

// 6.1获取场景div

export default {
  __name: 'SceneCanvas',
  setup(__props, {
    expose
  }) {
    expose();

    // 1.导入threejs
    // import * as THREE from 'three'
    // 2.导入(初始化)场景
    let sceneDiv = ref(null);

    // 3.2.将相机添加进场景
    scene.add(camera);

    // 5.2
    scene.add(axse);

    // 9.设置屏幕大小改变画布自适应
    window.addEventListener('resize', () => {
      // 更新摄像头
      camera.aspect = window.innerWidth / window.innerHeight;
      // 更新投影矩阵
      camera.updateProjectionMatrix();
      // 更新渲染器
      renderer.setSize(window.innerWidth, window.innerHeight);
      // 设置渲染器像素比例
      renderer.setPixelRatio(window.devicePixelRatio);
    });

    // // 8.1创建轨道控制器
    // const controls = new OrbitControls(camera, renderer.domElement)
    // // 8.2.设置控制阻尼
    // controls.enableDamping = true

    // vue挂载成功之后再去获取dom
    onMounted(() => {
      // 6.2将渲染器加入dom
      sceneDiv.value.appendChild(renderer.domElement);
      // 7.2渲染执行
      animate();
    });
    // 7.渲染器渲染场景
    // 7.1渲染声明
    // const clock = new THREE.Clock()
    function animate() {
      // const dateTime = clock.getDelta()
      // 控制器不起效果的原因是控制轨道每一帧都要更新
      controls.update();
      renderer.render(scene, camera);
      requestAnimationFrame(animate);
    }
    const __returned__ = {
      get sceneDiv() {
        return sceneDiv;
      },
      set sceneDiv(v) {
        sceneDiv = v;
      },
      animate,
      get scene() {
        return scene;
      },
      get camera() {
        return camera;
      },
      get renderer() {
        return renderer;
      },
      get controls() {
        return controls;
      },
      get axse() {
        return axse;
      },
      ref,
      onMounted
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
