import * as THREE from 'three'
// 飞线就是曲线
export default class FlyLine {
  constructor() {
    let linePoints = [
      new THREE.Vector3(0, 0, 0),
      new THREE.Vector3(4, 40, 0),
      new THREE.Vector3(8, 0, 0),
    ]
    // 创建平滑的贝塞尔光滑三维曲线
    this.lineCurve = new THREE.CubicBezierCurve3(linePoints)
    // 根据曲线生成管道几何
    this.geomentry = new THREE.TubeGeometry(this.lineCurve, 100, 1, 2, false)
    // 创建飞线材质
    this.material = new THREE.MeshBasicMaterial({color: 0xfff000})

    // 创建飞线物体
    this.mesh = new THREE.Mesh(this.geomentry, this.material)
  }
}
