// 1.导入threejs
import * as THREE from 'three'
// 2.初始化场景
const scene = new THREE.Scene()

// 场景天空
const textureLoader = new THREE.CubeTextureLoader()
const texture = textureLoader.load([
  './texture/1.jpg',
])


// 3.导出场景供其他组件使用
export default scene
