import * as THREE from 'three'
// 飞线就是曲线
export default class FlyLine {
  constructor() {
    let linePoints = [
      new THREE.Vector3(0, 0, 0),
      new THREE.Vector3(40, 40, 0),
      new THREE.Vector3(80, 0, 0),
    ]
    // 创建平滑的贝塞尔光滑三维曲线
    this.lineCurve = new THREE.CubicBezierCurve3(linePoints)
  }
}
