import * as THREE from 'three'
import LineRader from './lineRader'
export default class ALarm {
  constructor () {
    const textureLoad = new THREE.TextureLoader()
    const texture =  textureLoad.load('./svg/警告.svg')
    this.material = new THREE.SpriteMaterial({ map: texture })

    this.mesh = new THREE.Sprite(this.material)

    this.mesh.position.set(-3, 1, 0.3)
    this.mesh.scale.set(0.3, 0.3, 0.3)

    // 只有射线射到警告这里，点击事件才能执行
    this.lineRader = new LineRader()
    this.mouse = new THREE.Vector2()
    // 封装点击事件
    this.fns = []

    // 事件监听
    window.addEventListener('click', event => {
      // -1到1
      this.mouse.x = (event.clientX / window.innerWidth) * 2 -1
      this.mouse.y = (event.clienty / window.innerHeight) * 2 -1
      this.fns.forEach(item => {
        item(event);
      })
    })
  }
  onClick(fn) {
    this.fns.push(fn)
  }
}
