import { DRACOLoader } from 'three/examples/jsm/loaders/DRACOLoader'
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader'
// import * as THREE from 'three'
import scene from '@/threeModal/scene'
import eventBus from '@/utils/eventBus'
// import gsap from 'gsap'
// import CameraModule from '@/threeModal/camera'
// import modityCityMaterial from '../modity/modityCityMaterial'
// import FlyLine from './flyLine'
// import FlyLineShader from './flyLineShader'
// import CreateMeshLine from './createMeshLine'
// import LineWall from './lineWall'
// import LineRader from './lineRader'
// import Alarm from './alarm'
export default class CreateCity {
  constructor () {
    let daoLoader = new DRACOLoader()
    let loader = new GLTFLoader()
    daoLoader.setDecoderPath('/glb/')
    daoLoader.setDecoderConfig({ type: 'js' })
    daoLoader.preload()
    loader.setDRACOLoader(daoLoader)
    // 储存模型
    this.this.modelArray = []
    // 加载大楼模型
    loader.load('/glb/工厂大楼.glb', gltf => {
      console.log(gltf)
      // gltf.scene.traverse(mesh => {

      // })
      gltf.scene.visible = true
      this.modelArray.push({ name: 'default', mesh: gltf.scene })
      scene.add(gltf.scene)
    })
    loader.load('/glb/工厂一楼.glb', gltf => {
      console.log(gltf)
      gltf.scene.traverse(mesh => {
        if (mesh.isMesh) {
          mesh.material.emissiveIntensity = 15
        }
      })
      gltf.scene.visible = false
      this.modelArray.push({ name: 'firstFloor', mesh: gltf.scene })
      scene.add(gltf.scene)
    })
    loader.load('/glb/工厂二楼.glb', gltf => {
      console.log(gltf)
      gltf.scene.traverse(mesh => {
        if (mesh.isMesh) {
          mesh.material.emissiveIntensity = 15
        }
      })
      gltf.scene.visible = false
      this.modelArray.push({ name: 'secondFloor', mesh: gltf.scene })
      scene.add(gltf.scene)
    })

  }
  toggleModel() {
    eventBus.on('toggleModel', name => {
      if (name !== 'hierarchy') {
        this.modelArray.forEach(data => {
          data.mesh.visible = false
        })
        this.modelArray.forEach(data => {
          if (data.name === name) {
            data.mesh.visible = true
          }
        })
      } else {
        this.modelArray.forEach((data, index) => {
          if(index === 0) {
            data.mesh.position.setY(200)
          }
          if(index === 1) {
            data.mesh.position.setY(0)
          }
          if(index === 2) {
            data.mesh.position.setY(100)
          }
          data.mesh.visible = true
        })
      }
    })
  }
  
}
