import * as THREE from 'three'
import gsap from 'gsap'
// 城市模型材质修改
export default function modityCityMaterial (city) {
  
  // 修改城市模型着色器
  city.material.onBeforeCompile = shader => {
    // 添加一个替换标识
    shader.fragmentShader = shader.fragmentShader.replace(
      '#include <dithering_fragment>',
      `#include <dithering_fragment>
          // #end#
          `
    )
    // 城市混合颜色效果
    addGradColor(city, shader)
    // 光圈扩散
    addSpread(shader)
  }
}
// 1.城市混合颜色效果
function addGradColor(city, shader) {
  // 先计算
  city.geometry.computeBoundingBox()
  // 解构得到外边界矩形
  let { max, min } = city.geometry.boundingBox
  // 最高点减去最低点得到高度差
  let uHeight = max.y - min.y
  // 最高点的颜色
  shader.uniforms.uTopColor = {value: new THREE.Color('#6699cc')}

  // 高度差
  shader.uniforms.uHeight = {value: uHeight}
  shader.vertexShader = shader.vertexShader.replace(
    '#include <common>', // 将顶点着色器的顶点传到片元着色器上
    // 这里定义一个属性，片元着色器才能获取
    `#include <common>
      varying vec3 vPosition; 
    `
  )
  shader.vertexShader = shader.vertexShader.replace(
    '#include <begin_vertex>', // 将顶点着色器的顶点传到片元着色器上
    // 将顶点着色器的position传到common定义的varying vposition，这样在片元着色器能获取到顶点着色器的position
    `#include <begin_vertex>
      vPosition = position;
      `
  )
  shader.fragmentShader = shader.fragmentShader.replace(
    '#include <common>',
    // 将材质传入的变量定义，将顶点传入的数据定义
    `#include <common>
        uniform vec3 uTopColor;
        uniform float uHeight;
        varying vec3 vPosition;
        `
  )
  shader.fragmentShader = shader.fragmentShader.replace(
    '// #end#',
    // 定义一个将要混合成的颜色变量，将片元着色器的颜色传入
    `
        vec4 distGradColor = gl_FragColor;
        // 设置混合百分比 uHeight 的差可能为负，所以永远为正 -84 + 84 必须等于 0 到 160   即 负除正等于 负乘正
        float gradMix = (vPosition.y + uHeight / 2.0) / uHeight; // 从0到高度  即从0到100%
        // 计算混合颜色
        vec3 gradMixColor = mix(distGradColor.xyz, uTopColor, gradMix);
        gl_FragColor = vec4(gradMixColor, 1);
        // #end#
        `
  )
}

// 2.类雷达效果
function addSpread(shader) {
  // 传入雷达中心点
  shader.uniforms.uSpreadCenter = {value: new THREE.Vector2(0, 0)}
  // 传入扩散时间
  shader.uniforms.uSpreadTime = {value: 0}
  // 传入条纹的宽度
  shader.uniforms.uSpreadWidth = {value: 30}

  shader.fragmentShader = shader.fragmentShader.replace(
    '#include <common>',
    // 将材质传入的变量定义，将顶点传入的数据定义
    `#include <common>
        uniform vec2 uSpreadCenter;
        uniform float uSpreadTime;
        uniform float uSpreadWidth;
        `
  )
  shader.fragmentShader = shader.fragmentShader.replace(
    '// #end#',
    `
        // 获得顶点到中心，即半径
        float spreadRadius = distance(vPosition.xz, uSpreadCenter);
        // 扩散范围的函数
        float spreadIndex = - (spreadRadius - uSpreadTime) * (spreadRadius - uSpreadTime) + uSpreadWidth;
        

        if(spreadIndex > 0.0) {
          gl_FragColor = mix(gl_FragColor, vec4(1, 1, 1, 1), spreadIndex / uSpreadWidth);
        }
        // #end#
        `
  )
  // 利用动画来让时间不断改变
  gsap.to(shader.uniforms.uSpreadTime, {
    value: 600,
    duration: 1,
    ease: 'none',
    repeat: -1
  })

}
