// 7.渲染器渲染场景
// 7.1渲染声明
const clock = new THREE.Clock()
export default function animate () {
  const dateTime = clock.getDelta()
  // 控制器不起效果的原因是控制轨道每一帧都要更新
  controls.update()
  renderer.render(scene, camera)
  requestAnimationFrame(animate)
