import "core-js/modules/es.array.push.js";
import scene from '@/threeModal/scene';
// 3.1导入相机配置
import camera from '@/threeModal/camera';
// 4.导入渲染器
import renderer from '@/threeModal/renderer';
// // 导入控制器
// import controls from '@/threeModal/controls'
// 导入创建物体文件
import createMesh from '@/threeModal/createMesh';
// 导入渲染更新
import animate from '@/threeModal/animate';
// 导入初始化监听屏幕大小
import '@/threeModal/init';
// 5.1导入坐标轴
import axse from '@/threeModal/axseHelp';
import { ref, onMounted, watch } from 'vue';
import Alarm from '@/threeModal/mesh/alarm';
import LineWall from '@/threeModal/mesh/lineWall';
import FlyLineShader from '@/threeModal/mesh/flyLineShader';
import LineRader from '@/threeModal/mesh/lineRader';
export default {
  __name: 'SceneCanvas',
  props: ['resCityList'],
  setup(__props, {
    expose
  }) {
    expose();
    const props = __props;

    // 1.导入threejs
    // import * as THREE from 'three'
    // 2.导入(初始化)场景

    // 7.1获取场景div
    let sceneDiv = ref(null);

    // 3.2.将相机添加进场景
    scene.add(camera);

    // 5.2 创建坐标轴
    scene.add(axse);

    // 6.创建物体
    createMesh();

    // vue挂载成功之后再去追加dom
    onMounted(() => {
      // 7.2将渲染器加入dom
      sceneDiv.value.appendChild(renderer.domElement);
      // 8.2渲染执行
      animate();
    });
    const meshDestroy = [];
    const meshFn = {
      火警: position => {
        // 光墙
        // 添加光墙
        const lineWall = new LineWall(0.3, 2, position);
        meshDestroy.push(lineWall);
        scene.add(lineWall.mesh);
      },
      治安: position => {
        // 光线
        // 添加光线
        const flyLineShader = new FlyLineShader(position);
        scene.add(flyLineShader.mesh);
        meshDestroy.push(flyLineShader);
      },
      电力: position => {
        // 雷达
        // 添加雷达
        const lineRader = new LineRader(1, position);
        scene.add(lineRader.mesh);
        meshDestroy.push(lineRader);
      }
    };
    watch(() => props.resCityList, () => {
      // 每次得到新的数据要销毁旧物体
      meshDestroy.forEach(item => {
        item.onRemove();
      });
      props.resCityList.forEach(item => {
        // console.log(item, i)
        //    返回的位置数据为0-100
        const position = {
          x: item.position.x / 15 - 3.5,
          z: item.position.z / 15 - 3.5
        };
        // 生成精灵图
        // 添加警告标识
        const alarm = new Alarm(item.name, position);
        // 将生成的物体放到数组，以待每次新数据传入生成新物体时销毁旧物体
        meshDestroy.push(alarm);
        scene.add(alarm.mesh);
        if (meshFn[item.name]) {
          meshFn[item.name](position);
        }
      });
    });
    const __returned__ = {
      props,
      get sceneDiv() {
        return sceneDiv;
      },
      set sceneDiv(v) {
        sceneDiv = v;
      },
      meshDestroy,
      meshFn,
      get scene() {
        return scene;
      },
      get camera() {
        return camera;
      },
      get renderer() {
        return renderer;
      },
      get createMesh() {
        return createMesh;
      },
      get animate() {
        return animate;
      },
      get axse() {
        return axse;
      },
      ref,
      onMounted,
      watch,
      get Alarm() {
        return Alarm;
      },
      get LineWall() {
        return LineWall;
      },
      get FlyLineShader() {
        return FlyLineShader;
      },
      get LineRader() {
        return LineRader;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
